let read = global.get("weather")

msg = {
    payload: read.description
}
return msg;

let sun_time = msg.payload.sunrise;
let sun_time_set = msg.payload.sunset

let rise_date = new Date(sun_time * 1000);
let set_date = new Date(sun_time_set * 1000);

let sunrise = rise_date.toLocaleTimeString("en-GB", { hour: "2-digit", minute: "2-digit", second: "2-digit", hour12: false });
let sunset = set_date.toLocaleTimeString("en-GB", { hour: "2-digit", minute: "2-digit", second: "2-digit", hour12: false });

msg.payload.sunrise_time = sunrise
msg.payload.sunset_time = sunset
return msg;

