let readings = global.get("weather");
let limit_temp = flow.get("warning_temp")

let temp = readings.tempc;
let diff = temp - limit_temp;

if (temp >= limit_temp){
    msg ={
        payload: `Warning! Temperature is ${temp}C. Exceeds the limit by ${diff.toFixed(2)}C `
    }
    return msg;
}

let readings = global.get("weather");
let limit_hum = flow.get("warning_hum")

let hum = readings.humidity;
let diff = hum - limit_hum;

if (hum >= limit_hum){
    msg ={
        payload: `Warning! Humidity is ${hum}%. Exceeds the limit by ${diff.toFixed(2)}% `
    }
    return msg;
}

let readings = global.get("weather");
let limit_pres = flow.get("warning_pres")

let pres = readings.pressure;
let pres_atm = pres / 1013.25;
let diff = pres_atm - limit_pres;

if (pres_atm >= limit_pres){
    msg ={
        payload: `Warning! Pressure is ${pres_atm.toFixed(2)}atm. Exceeds the limit by ${diff.toFixed(2)}atm `
    }
    return msg;
}

let readings = global.get("weather");
let limit_wind = flow.get("warning_wind")

let wind = readings.windspeed;
let diff = wind - limit_wind;

if (wind >= limit_wind){
    msg ={
        payload: `Warning! Wind speed is ${wind}m/s. Exceeds the limit by ${diff.toFixed(2)}m/s `
    }
    return msg;
}
